package Repaso;

public class UsoBucleFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//for ascendente
		for(int i=0;i<10;i++) {
			
			System.out.println("Esther");
			
		}
		System.out.println("");
		//for con decremento
		for(int i=10;i>0;i--) {
			
			System.out.println("Esther");
			
		}
		System.out.println("");
		//Esta vez de dos en dos hasta 20
		for(int i=0;i<20;i+=2) {
			
			System.out.println("Esther");
			
		}

	}

}
